package MyFirstPackage;

public class Datatypes {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		byte a;//initialization
		a=100;//declaration
		System.out.println(a);
		
		short b;//initialization
		b=1123;//declaration
		System.out.println(b);

		int c;//initialization
		c=223456;//declaration
		System.out.println(c);
		
		long d;//initialization
		d=987654326;//declaration
		System.out.println(d);

		double e;//initialization
		e=6876987;//declaration
		System.out.println(e);
		
		float f;//initialization
		f=3;//declaration
		System.out.println(f);
		
		char g;//initialization
		g=101;//declaration
		System.out.println(g);
		
		boolean h;//initialization
		h=true;//declaration
		System.out.println(h);
		



	}

}
